def plot_roc_curve(fpr, tpr):
    plt.rc('font', size=16, family='Bitstream Vera Sans')
    plt.rc('text', usetex=True)
    plt.plot(fpr, tpr, color='orange', label='MB-SEM')
    plt.plot([0, 1], [0, 1], color='darkblue', linestyle='--', label='Expected from random classifier')
    plt.xlabel('False Positive Rate')
    plt.ylabel('True Positive Rate')
    plt.legend()
    plt.savefig('roc.pdf')

fpr, tpr, thresholds = roc_curve(truths, props)
